#include "AuthCertificateBase.h"

using namespace WONAuth;

///////////////////////////////////////////////////////////////////////////////
///////////////////////////////////////////////////////////////////////////////

// Inlines
AuthCertificateBase::AuthCertificateBase() :
	AuthFamilyBuffer(),
	mUserId(0)
{}


///////////////////////////////////////////////////////////////////////////////
///////////////////////////////////////////////////////////////////////////////
AuthCertificateBase::AuthCertificateBase(const AuthCertificateBase& theCertR) :
	AuthFamilyBuffer(theCertR),
	mUserId(theCertR.mUserId),
	mPubKey(theCertR.mPubKey),
	mAccessList(theCertR.mAccessList)
{}

///////////////////////////////////////////////////////////////////////////////
///////////////////////////////////////////////////////////////////////////////
AuthCertificateBase::~AuthCertificateBase()
{}

///////////////////////////////////////////////////////////////////////////////
///////////////////////////////////////////////////////////////////////////////
AuthCertificateBase& AuthCertificateBase::operator=(const AuthCertificateBase& theCertR)
{ 
	if(this!=&theCertR)
	{
		AuthFamilyBuffer::operator=(theCertR);  
		mUserId = theCertR.mUserId;
		mPubKey = theCertR.mPubKey;
		mAccessList = theCertR.mAccessList;
	}
	return *this; 
}

