///////////////////////////////////////////////////////////////////////////
///////////////////////////////////////////////////////////////////////////

BigInteger::BigInteger(__int64 theFrom, bool negative)
{
	RawBuffer aBuf;
	unsigned __int64 from;

	if(negative) 
		from = -theFrom;
	else
		from = theFrom;

	unsigned char *start = ((unsigned char*)&from) + 7;

	for(int i=0; i<8; i++)
	{
		aBuf+=*start;

		start--;
	}

	fromBinary(aBuf);

	this->negative = negative;
}

